sys_script_include.script:
var commonUtils = Class.create();
commonUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getVendorInfo: function(){
		var vendor = this.getParameter('paramVendor');
		var gr = new GlideRecord("core_company");
		gr.get(vendor);

		var address = gr.street.getDisplayValue();
		var city = gr.city.getDisplayValue();
		var cap = gr.zip.getDisplayValue();
		var phone = gr.phone.getDisplayValue();

		var response = {};
		response.address = address;
		response.city = city;
		response.cap = cap;
		response.phone = phone;

		return JSON.stringify(response);
	},

	type: 'commonUtils'
});
