sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 */
function handler({api, event, helpers, imports}) {
    const eventName = event.name;
    var navigationPayload = {};

    if (eventName == 'NOW_CONTENT_TREE#ACTIONABLE_ITEM_CLICKED')
    {
        navigationPayload = {
            route: 'record',
            fields: {
                table: 'sn_grc_taxonomy_internal_taxonomy',
                sysId: event.payload.item.id
            },
        };
    }
    api.emit("NAV_ITEM_SELECTED", navigationPayload);
}
