sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
async function handler({
    api,
    event,
    helpers,
    imports
}) {
    const {
        table,
        vendor,
        applies_to,
        engagement
    } = api.data.get_table_vendor_engagement.output;


    if (vendor)
        api.setState('sysId1', vendor);

    if (engagement) {
        const engagementTitle = await helpers.translate('Engagement overview');
        api.setState('table', 'sn_vdr_risk_asmt_vendor_engagement');
        api.setState('sysId2', engagement);
        api.setState('title', engagementTitle);
        api.data.get_engagement_details.refresh();
        api.setState('displayType', 'engagement');
    } else {
        const vendorTitle = await helpers.translate('Vendor overview');
        api.setState('table', 'core_company');
        api.setState('title', vendorTitle);
        api.data.get_vendor_details.refresh();
        api.setState('displayType', 'vendor');
    }
}
