sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 */
function handler({api, event, helpers, imports}) {

    if(event.elementId == 'checkbox_1'){
        api.setState('createOp',event.payload.value?'true':'false');
    }
    else if(event.elementId == 'checkbox_3'){
        api.setState('updateOp',event.payload.value?'true':'false');
    }
    else if(event.elementId == 'checkbox_4'){
        api.setState('deleteOp',event.payload.value?'true':'false');
    }
    else if(event.elementId == 'dropdown_1'){
        api.setState('info_object',event.payload.item.id);
    }
}
